let labels=[],amount=[],colors=[];$("table>tbody>tr").each((function(){labels.push($(this).children("td:nth-child(2)").text().trim()),amount.push($(this).children("td:nth-child(3)").attr("data-amount"))})),labels.forEach(()=>{colors.push("#"+(16777215*Math.random()<<0).toString(16).padStart(6,"0"))});const data={labels:labels,datasets:[{label:"Amount: ",data:amount,backgroundColor:colors}]},config={type:"bar",data:data,options:{scales:{y:{beginAtZero:!0}},plugins:{title:{display:!0,text:"Current Balances"},legend:{display:!1}}}},barChart=new Chart(document.getElementById("bar-index"),config);